$world = new World();
\Feature\Feature::create($world, $features);
}
private function transformFeatures($features, $value, $key)
{
$features[$value['key']] = $value;
$features[$value['key']]['users'] = (isset($value['variants']['users'])) ? $value['variants']['users'] : [];
return $features;
}

