-&gt;setRefreshToken(Arr::get($response, 'refresh_token'))
-&gt;setExpiresIn(Arr::get($response, 'expires_in'));
}
</code></pre><p>This is in the <code>vendor/laravel/socialite/src/Two/AbstractProvider.php</code></p>
