$this-&gt;client-&gt;request(
'POST',
$this-&gt;getSlackUrl(),
[
'body' =&gt; $this-&gt;message($message)
]
);
} catch (\Exception $e) {
Log::debug(sprintf(&quot;Error sending to Slack %s&quot;, $e-&gt;getMessage()));
}
}
protected function message($message)
{
return json_encode(
[
'text' =&gt; $message
]
);
}
