$this-&gt;assertEquals(&quot;bar2&quot;, $results-&gt;getPfawsYamlEnv('aws_key'));
$this-&gt;assertEquals(&quot;foo2&quot;, $results-&gt;getPfawsYamlEnv('aws_secret'));
